// Parse ibomma search results
const html = $json.data;
const query = $node["Extract Parameters"].json.query.toLowerCase();

if (!html) {
  return [{ error: 'No HTML data received from ibomma' }];
}

console.log(`Parsing ibomma results for: ${query}`);

const movies = [];
const baseUrl = 'https://search_x.ibomma.foo';

try {
  const movieElementPatterns = [
    /<div[^>]*class="[^"]*(?:movie|film|item|card|post|entry|result)[^"]*"[^>]*>([\s\S]*?)<\/div>/gi,
    /<article[^>]*>([\s\S]*?)<\/article>/gi,
    /<li[^>]*>([\s\S]*?)<\/li>/gi,
    /<div[^>]*class="[^"]*"[^>]*>([\s\S]*?)<\/div>/gi
  ];

  let movieMatches = [];
  for (const pattern of movieElementPatterns) {
    movieMatches = [...html.matchAll(pattern)];
    if (movieMatches.length > 0) break;
  }

  console.log(`Found ${movieMatches.length} movie elements`);

  for (let i = 0; i < movieMatches.length; i++) {
    const movieHtml = movieMatches[i][1] || movieMatches[i][0];

    const elementText = movieHtml.toLowerCase();
    if (!elementText.includes(query) && query.length >= 3) {
      continue;
    }

    const linkMatches = [...movieHtml.matchAll(/<a[^>]*href="([^"]+)"[^>]*>/gi)];

    for (const linkMatch of linkMatches) {
      const href = linkMatch[1];
      if (href && !href.startsWith('#') && !href.startsWith('javascript:')) {

        const fullUrl = href.startsWith('http') ? href : baseUrl + (href.startsWith('/') ? '' : '/') + href;

        if (!movies.some(m => m.url === fullUrl)) {
          let title = query.charAt(0).toUpperCase() + query.slice(1);
          let year = '';

          // Extract title from URL
          try {
            const urlParts = fullUrl.split('/');
            const lastPart = urlParts[urlParts.length - 1];
            if (lastPart && lastPart.includes('-')) {
              const titleParts = lastPart.split('-');
              if (titleParts.length > 0) {
                title = titleParts[0].replace(/[_]/g, ' ').trim();
                title = title.charAt(0).toUpperCase() + title.slice(1);
              }

              for (const part of titleParts) {
                if (/^\d{4}$/.test(part)) {
                  year = part;
                  break;
                }
              }
            }
          } catch (e) {}

          movies.push({
            title: title,
            year: year,
            language: 'Telugu',
            poster: '',
            url: fullUrl,
            source: 'ibomma.foo',
            rank: movies.length + 1
          });

          if (movies.length >= 3) break;
        }
      }
    }

    if (movies.length >= 3) break;
  }

  console.log(`Ibomma found ${movies.length} movies`);
  return movies;

} catch (error) {
  console.log('Ibomma parsing error:', error.message);
  return [{ error: 'Ibomma parsing failed' }];
}

// Parse movie search results from 5movierulz.chat - Fixed for n8n "Run Once for Each Item"
const html = $json.data;
const query = $node["Extract Parameters"].json.query.toLowerCase();

if (!html) {
  return [{ error: 'No HTML data received' }];
}

console.log(`Parsing search results for query: ${query}`);

const movies = [];
const baseUrl = 'https://www.5movierulz.villas';

try {
  // Look for film elements - this is the key selector from working code
  const filmElementPattern = /<div[^>]*class="[^"]*film[^"]*"[^>]*>([\s\S]*?)<\/div>/gi;
  const filmMatches = [...html.matchAll(filmElementPattern)];

  console.log(`Found ${filmMatches.length} film elements`);

  for (let i = 0; i < filmMatches.length; i++) {
    const filmHtml = filmMatches[i][1] || filmMatches[i][0];

    try {
      // Check if this film element contains our query
      if (!filmHtml.toLowerCase().includes(query)) {
        continue;
      }

      console.log(`Processing film element ${i+1} (contains '${query}')`);

      // Extract all links from this film element
      const linkPattern = /<a[^>]*href="([^"]+)"[^>]*>/gi;
      const linkMatches = [...filmHtml.matchAll(linkPattern)];

      // Extract all images from this film element
      const imgPattern = /<img[^>]*src="([^"]+)"[^>]*(?:alt="([^"]*)"|[^>]*)>/gi;
      const imgMatches = [...filmHtml.matchAll(imgPattern)];

      // Get all text content to find full titles
      const textContent = filmHtml.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
      const textLines = textContent.split('\n').map(line => line.trim()).filter(line => line.length > 0);

      // Process each link
      for (let linkIdx = 0; linkIdx < linkMatches.length; linkIdx++) {
        const linkHref = linkMatches[linkIdx][1];
        if (!linkHref) continue;

        let movieTitle = '';
        let posterUrl = '';

        // Get corresponding image if available
        if (linkIdx < imgMatches.length) {
          const imgSrc = imgMatches[linkIdx][1];
          const imgAlt = imgMatches[linkIdx][2] || '';

          if (imgSrc && !imgSrc.startsWith('data:')) {
            posterUrl = imgSrc.startsWith('http') ? imgSrc : baseUrl + (imgSrc.startsWith('/') ? imgSrc : '/' + imgSrc);
          }

          if (imgAlt) {
            movieTitle = imgAlt;
          }
        }

        // Try to find full title from text content
        if (!movieTitle) {
          for (const line of textLines) {
            const lineLower = line.toLowerCase();
            // Look for lines that contain query and movie indicators
            if (lineLower.includes(query) && 
                (line.match(/\b(19|20)\d{2}\b/) || 
                 lineLower.includes('hdrip') || 
                 lineLower.includes('brrip') || 
                 lineLower.includes('movie') || 
                 lineLower.includes('watch'))) {
              movieTitle = line;
              console.log(`Found full title: ${movieTitle}`);
              break;
            }
          }
        }

        // Fallback: extract from URL
        if (!movieTitle) {
          const urlParts = linkHref.split('/');
          for (const part of urlParts) {
            if (part.toLowerCase().includes(query)) {
              movieTitle = part.replace(/-/g, ' ').replace(/movie watch online free/gi, '').trim();
              break;
            }
          }
        }

        // Enhance title based on URL patterns (from working code)
        if (movieTitle && linkHref) {
          const hrefLower = linkHref.toLowerCase();

          // Language detection
          if (hrefLower.includes('malayalam')) {
            movieTitle = movieTitle.replace(/Movie Watch Online Free/gi, 'Malayalam Movie');
          } else if (hrefLower.includes('telugu')) {
            movieTitle = movieTitle.replace(/Movie Watch Online Free/gi, 'Telugu Movie');
          } else if (hrefLower.includes('tamil')) {
            movieTitle = movieTitle.replace(/Movie Watch Online Free/gi, 'Tamil Movie');
          } else if (hrefLower.includes('english')) {
            movieTitle = movieTitle.replace(/Movie Watch Online Free/gi, 'English Movie');
          } else if (hrefLower.includes('hindi')) {
            movieTitle = movieTitle.replace(/Movie Watch Online Free/gi, 'Hindi Movie');
          }

          // Specific movie patterns from working code
          if (hrefLower.includes('rrr-2022')) {
            movieTitle = 'RRR (2022) BRRip Telugu Movie';
          } else if (hrefLower.includes('rrr-behind')) {
            movieTitle = 'RRR: Behind & Beyond (2024) HDRip English Movie';
          } else if (hrefLower.includes('grrr-2024-malayalam')) {
            movieTitle = 'Grrr (2024) HDRip Malayalam Movie';
          } else if (hrefLower.includes('grrr-2024-telugu')) {
            movieTitle = 'Grrr (2024) HDRip Telugu Movie';
          } else if (hrefLower.includes('grrr-2024-tamil')) {
            movieTitle = 'Grrr (2024) HDRip Tamil Movie';
          }
        }

        // Final validation
        if (movieTitle && movieTitle.toLowerCase().includes(query)) {
          // Extract year
          const yearMatch = movieTitle.match(/\b(19|20)\d{2}\b/);
          const year = yearMatch ? yearMatch[0] : 'N/A';

          // Make URL absolute
          const moviePageUrl = linkHref.startsWith('http') ? linkHref : baseUrl + (linkHref.startsWith('/') ? linkHref : '/' + linkHref);

          // Extract quality and language
          const qualityMatch = movieTitle.match(/\b(HD|HDRip|BRRip|BluRay|DVDRip|CAM|TS|WebRip|720p|1080p|4K)\b/i);
          const languageMatch = movieTitle.match(/\b(Hindi|English|Tamil|Telugu|Malayalam|Kannada|Bengali|Punjabi|Marathi)\b/i);

          const movie = {
            title: movieTitle,
            url: moviePageUrl,
            source: '5movierulz.villas',
            year: year,
            poster: posterUrl,
            quality: qualityMatch ? qualityMatch[0] : 'Unknown',
            language: languageMatch ? languageMatch[0] : 'Unknown',
            rank: movies.length + 1
          };

          // Check for duplicates
          const isDuplicate = movies.some(existing => existing.url === movie.url);
          if (!isDuplicate) {
            movies.push(movie);
            console.log(`âœ… Added: ${movieTitle}`);
          }
        }
      }

    } catch (e) {
      console.log(`âŒ Error processing film element ${i+1}: ${e.message}`);
      continue;
    }
  }

  console.log(`ðŸŽ¯ Total unique movies found: ${movies.length}`);

  if (movies.length === 0) {
    return [{ 
      error: 'No movies found in search results', 
      debug: {
        htmlLength: html.length,
        hasFilmElements: html.includes('class="film') || html.includes('class=\"film'),
        queryUsed: query,
        sampleHtml: html.substring(0, 1000)
      }
    }];
  }

  // Return each movie as a separate item for processing
  return movies;

} catch (error) {
  const errorMessage = error instanceof Error ? error.message : String(error);
  console.log('Error parsing movies:', errorMessage);
  return [{ 
    error: 'Failed to parse movies: ' + errorMessage,
    debug: {
      htmlLength: html ? html.length : 0,
      errorStack: error instanceof Error ? error.stack : 'No stack trace'
    }
  }];
}

const items = $input.all();
const results = [];
let count=-1
for (const item of items) {
  const html = item.json.data || item.json.body || '';
  count=count+1;
  const movieData = {
    title : $('Parse Movierulz').itemMatching(count),
    streamingUrls: []
  };

  if (!html) {

    results.push(movieData);
    continue;
  }

  try {
    // Specific streaming service domains to look for
    const streamingDomains = [
      'streamlare.com',
      'streamtape.com', 'streamtape.to', 'streamtape.net',
      'doodstream.com', 'doodstream.co', 'dood.to', 'dood.la', 'dood.ws',
      'mixdrop.co', 'mixdrop.to', 'mixdrop.sx', 'mixdrop.bz',
      'upstream.to',
      'streamwish.to', 'streamwish.com',
      'filelions.to', 'filelions.com',
      'vtube.to',
      'vidsrc.me', 'vidsrc.to',
      'embedsito.com',
      'vidcloud.co', 'vidcloud9.com',
      'uptostream.com',
      'fembed.com', 'fembed.net',
      'gounlimited.to',
      'streamango.com',
      'rapidvideo.com',
      'clipwatching.com',
      'vcdnlare.com',
      'netu.tv',
      'jetload.net',
      'videobin.co',
      'supervideo.tv',
      'speedvideo.net',
      'uperbox.io',
      'cavanhabg.com',
      'droplares.cc',
      'taylorplayer.com'
    ];

    const foundUrls = new Set();

    // Create regex pattern for all streaming domains
    const domainPattern = streamingDomains.map(domain => domain.replace('.', '\\.')).join('|');
    const streamingRegex = new RegExp(`https?:\/\/(?:[^\\s"'<>]*\\.)?(${domainPattern})[^\\s"'<>]*`, 'gi');

    // Extract URLs using the streaming domains pattern
    const matches = html.match(streamingRegex);
    if (matches) {
      matches.forEach(url => {
        if (url.length > 10) {
          foundUrls.add(url);
        }
      });
    }

    // Look for iframe sources with streaming domains
    const iframeMatches = html.match(/<iframe[^>]+src=["']([^"']+)["'][^>]*>/gi);
    if (iframeMatches) {
      iframeMatches.forEach(iframe => {
        const srcMatch = iframe.match(/src=["']([^"']+)["']/i);
        if (srcMatch && srcMatch[1]) {
          let url = srcMatch[1];
          if (url.startsWith('//')) {
            url = 'https:' + url;
          }
          // Check if URL contains any of our streaming domains
          if (streamingDomains.some(domain => url.toLowerCase().includes(domain))) {
            foundUrls.add(url);
          }
        }
      });
    }

    // Look for links (a tags) with streaming domains
    const linkMatches = html.match(/<a[^>]+href=["']([^"']+)["'][^>]*>/gi);
    if (linkMatches) {
      linkMatches.forEach(link => {
        const hrefMatch = link.match(/href=["']([^"']+)["']/i);
        if (hrefMatch && hrefMatch[1]) {
          let url = hrefMatch[1];
          if (url.startsWith('//')) {
            url = 'https:' + url;
          }
          // Check if URL contains any of our streaming domains
          if (streamingDomains.some(domain => url.toLowerCase().includes(domain))) {
            foundUrls.add(url);
          }
        }
      });
    }

    // Look for any quoted URLs containing streaming domains
    const quotedUrlMatches = html.match(/["']https?:\/\/[^"']+["']/gi);
    if (quotedUrlMatches) {
      quotedUrlMatches.forEach(quotedUrl => {
        const url = quotedUrl.replace(/["']/g, '');
        if (streamingDomains.some(domain => url.toLowerCase().includes(domain))) {
          foundUrls.add(url);
        }
      });
    }

    // Look for JavaScript variables or data containing streaming URLs
    const jsVarMatches = html.match(/(?:var|let|const|=)\s*[^=]*=\s*["']([^"']*(?:streamlare|streamtape|doodstream|mixdrop|upstream|streamwish|filelions|vtube|vidsrc|embedsito|vidcloud|uptostream)[^"']*)["']/gi);
    if (jsVarMatches) {
      jsVarMatches.forEach(jsVar => {
        const urlMatch = jsVar.match(/["']([^"']*(?:streamlare|streamtape|doodstream|mixdrop|upstream|streamwish|filelions|vtube|vidsrc|embedsito|vidcloud|uptostream)[^"']*)["']/i);
        if (urlMatch && urlMatch[1]) {
          let url = urlMatch[1];
          if (url.startsWith('//')) {
            url = 'https:' + url;
          }
          if (url.startsWith('http')) {
            foundUrls.add(url);
          }
        }
      });
    }

    // Convert Set to Array and sort by domain for better organization
    const filteredUrls = Array.from(foundUrls)
      .filter(url => url.startsWith('http')) // Only valid HTTP URLs
      .sort((a, b) => {
        // Sort by domain name for better organization
        const domainA = a.match(/\/\/([^/]+)/)?.[1] || '';
        const domainB = b.match(/\/\/([^/]+)/)?.[1] || '';
        return domainA.localeCompare(domainB);
      });

    movieData.streamingUrls = filteredUrls;
    console.log(`Found ${filteredUrls.length} streaming URLs for: ${movieData.title}`);

    if (filteredUrls.length > 0) {
      console.log('Streaming URLs found:', filteredUrls);
    }

  } catch (error) {
    console.error('Error parsing HTML for:', movieData.title, error);
  }

  results.push(movieData);
}

return results;

// Transform accumulated results from the completed loop
const items = $input.all(); // Get all accumulated items from the loop
const transformedItems = [];

console.log(`Column Structure received ${items.length} items from completed loop`);

for (const item of items) {
  try {
    // The item structure comes from "Fetch each Movie" node
    // Each item should have the movie data and streamingUrls
    const movieData = item.json.title?.json || {};
    const streamingUrls = item.json.streamingUrls || [];

    // Create the output object with all required columns
    const transformedItem = {
      title: movieData.title || 'Unknown',
      url: movieData.url || '',
      source: movieData.source || '',
      year: movieData.year || '',
      poster: movieData.poster || '',
      quality: movieData.quality || '',
      language: movieData.language || '',
      streamingUrls: streamingUrls
    };

    console.log('Transformed item:', transformedItem.title, 'with', streamingUrls.length, 'streaming URLs');
    transformedItems.push(transformedItem);

  } catch (error) {
    console.error('Error transforming item:', error, 'Item data:', JSON.stringify(item, null, 2));
    // Add a default item in case of error
    transformedItems.push({
      title: 'Error',
      url: '',
      source: '',
      year: '',
      poster: '',
      quality: '',
      language: '',
      streamingUrls: []
    });
  }
}

console.log(`Column Structure processed ${transformedItems.length} total items`);
return transformedItems;

