// Extract movie download request data from webhook
// Handle both direct JSON and nested body structure
let requestData = $json;

// If data is nested in body, extract it
if ($json.body && typeof $json.body === 'string') {
  try {
    requestData = JSON.parse($json.body);
  } catch (e) {
    requestData = $json.body;
  }
} else if ($json.body && typeof $json.body === 'object') {
  requestData = $json.body;
}

// Extract the actual data
const movieUrl = requestData.movieUrl || $json.movieUrl;
const title = requestData.title || $json.title || 'Unknown Movie';
const action = requestData.action || $json.action || 'download';
const outputFormat = requestData.outputFormat || $json.outputFormat || 'mp4';

console.log('Received webhook data:', JSON.stringify($json, null, 2));
console.log('Extracted movieUrl:', movieUrl);
console.log('Extracted title:', title);

// Validate input
if (!movieUrl) {
  console.log('Available data keys:', Object.keys($json));
  throw new Error(`Movie URL is required. Received data: ${JSON.stringify($json)}`);
}

// Check if URL is from supported sources
const supportedSources = ['ibomma', '5movierulz', 'movierulz'];
const isSupported = supportedSources.some(source => 
  movieUrl.toLowerCase().includes(source)
);

if (!isSupported) {
  throw new Error('Download is only supported for iBomma and MovieRulz sources');
}

// Prepare Python script execution parameters
const pythonScriptPath = '/Users/vamsi/Desktop/Movie_Agent/github_dir/video_extraction/simple_video_extractor.py';
const cleanTitle = title.replace(/[^a-zA-Z0-9\s]/g, '').replace(/\s+/g, '_');
const outputFileName = `${cleanTitle}.${outputFormat}`;

return {
  movieUrl,
  title,
  action,
  outputFormat,
  pythonScriptPath,
  outputFileName,
  timestamp: new Date().toISOString(),
  status: 'initialized',
  debug: {
    originalData: $json,
    extractedData: requestData
  }
};

// Process the Python Code node output
const result = $json;

// Check if we already have a processed result from Python Code node
if (result.status) {
  // Result is already processed by Python Code node
  const stdout = result.stdout || '';
  const stderr = result.stderr || '';
  const exitCode = result.exit_code || 0;

  // Parse progress information from stdout
  const lines = stdout.split('\n').filter(line => line.trim());
  const progressInfo = [];
  let fileSize = null;
  let downloadProgress = null;

  // Extract useful information from output
  lines.forEach(line => {
    // Extract file size information
    const sizeMatch = line.match(/File size: ([\d.]+) MB \(([\d,]+) bytes\)/);
    if (sizeMatch) {
      fileSize = {
        mb: parseFloat(sizeMatch[1]),
        bytes: parseInt(sizeMatch[2].replace(/,/g, ''))
      };
    }

    // Extract progress information
    const progressMatch = line.match(/Progress: ([\d.]+)% \(([\d.]+)\/([\d.]+) MB\)/);
    if (progressMatch) {
      downloadProgress = {
        percentage: parseFloat(progressMatch[1]),
        downloadedMB: parseFloat(progressMatch[2]),
        totalMB: parseFloat(progressMatch[3])
      };
    }

    // Add line to progress info
    if (line.trim()) {
      progressInfo.push({
        message: line.trim(),
        timestamp: new Date().toISOString()
      });
    }
  });

  // Include stderr if there are errors
  if (stderr && stderr.trim()) {
    progressInfo.push({
      message: `Error: ${stderr.trim()}`,
      timestamp: new Date().toISOString(),
      type: 'error'
    });
  }

  return {
    status: result.status,
    message: result.message || (result.status === 'completed' ? 'Movie downloaded successfully' : 'Download failed'),
    exitCode: exitCode,
    fileSize,
    downloadProgress,
    progressInfo,
    stdout: stdout.substring(0, 1000), // Limit stdout size
    stderr: stderr.substring(0, 500),  // Limit stderr size
    timestamp: new Date().toISOString(),
    movieUrl: result.movie_url,
    title: result.title,
    outputFileName: result.output_filename
  };
} else {
  // Fallback for unexpected format
  return {
    status: 'error',
    message: 'Unexpected response format from Python execution',
    timestamp: new Date().toISOString(),
    rawResult: result
  };
}

// Handle errors and send error response
const error = $json.error || 'Unknown error occurred';
const movieUrl = $('Process Request').item?.json?.movieUrl || 'Unknown';
const title = $('Process Request').item?.json?.title || 'Unknown';

return {
  status: 'error',
  message: `Download failed: ${error}`,
  error: error,
  movieUrl,
  title,
  timestamp: new Date().toISOString()
};
